import React, { useState, useCallback } from 'react'
import { TouchableOpacity } from 'react-native';
import { View, Image, ImageBackground, Text, ScrollView } from 'react-native'
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import styles from '../App.styles';
import { TextInput } from 'react-native';
import { useFonts } from 'expo-font';
import { Ionicons, FontAwesome, FontAwesome5, Entypo, EvilIcons, Feather, MaterialCommunityIcons, MaterialIcons, AntDesign } from "react-native-vector-icons"

function TopBar({ navigation }) {

    const [fontsLoaded] = useFonts({
        'Kaushan': require('../assets/fonts/KaushanScript-Regular.ttf'),
        "Allura": require('../assets/fonts/Allura-Regular.ttf'),
        "Montserrat-Black": require('../assets/fonts/Montserrat-Black.ttf'),
        "Montserrat-BlackItalic": require('../assets/fonts/Montserrat-BlackItalic.ttf'),
        "Montserrat-Bold": require('../assets/fonts/Montserrat-Bold.ttf'),
        "Montserrat-BoldItalic": require('../assets/fonts/Montserrat-BoldItalic.ttf'),
        "Montserrat-Italic": require('../assets/fonts/Montserrat-Italic.ttf'),
        "Montserrat-Light": require('../assets/fonts/Montserrat-Light.ttf'),
        "Montserrat-LightItalic": require('../assets/fonts/Montserrat-LightItalic.ttf'),
        "Montserrat-Medium": require('../assets/fonts/Montserrat-Medium.ttf'),
        "Montserrat-MediumItalic": require('../assets/fonts/Montserrat-MediumItalic.ttf'),
        "Montserrat-Regular": require('../assets/fonts/Montserrat-Regular.ttf'),
        "Montserrat-Thin": require('../assets/fonts/Montserrat-Thin.ttf'),
        "Montserrat-SemiBold": require('../assets/fonts/Montserrat-SemiBold.ttf'),
        "Montserrat-SemiBoldItalic": require('../assets/fonts/Montserrat-SemiBoldItalic.ttf'),
        "Montserrat-ThinItalic": require('../assets/fonts/Montserrat-ThinItalic.ttf'),
    });

    const onLayoutRootView = useCallback(async () => {
        if (fontsLoaded) {
            await SplashScreen.hideAsync();
        }
    }, [fontsLoaded]);

    if (!fontsLoaded) {
        return null;
    }

    return (

        <View style={styles.HomepageTopbar}>

            <TouchableOpacity onPress={() => { navigation.navigate("Homepage") }} style={{ height: "100%", justifyContent: "center", }}>
                <Text style={{ fontSize: 18, fontFamily: "Kaushan", color: "#f7ede4", }}>HEROSCOPE</Text>
            </TouchableOpacity>

            <View style={{ height: "100%", justifyContent: "center", }}>

                <TouchableOpacity onPress={() => { navigation.navigate("Profile") }} style={{ width: 40, height: 40, borderRadius: 100, alignItems: "center", justifyContent: "center" }}>
                    <Image
                        resizeMode='contain'
                        style={{ width: "100%", height: "100%", borderRadius: 100 }}
                        source={require("../img/kÄ±z.jpg")}
                    />
                </TouchableOpacity>

            </View>

        </View>
    )
}
export default TopBar;
