                <Fontisto size={20} name={"search"} />
            </View>

            <TextInput placeholderTextColor={"black"} style={{ width: "100%", borderRadius: 100, backgroundColor: "#9f9f9faa", fontSize: 11, minHeight: 35, paddingHorizontal: 35 }} placeholder='Arama YapÄ±n...' />
        </View>

    )
}
